import React, { Component } from 'react';
import Slider from 'react-slick';
import types from 'prop-types';

// Styles

import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import './styles.scss';

// ----------------

class HeroSlider extends Component {
    // Type of props

    static propTypes = {
        prop: types.string,
    };

    // Default value for props

    static defaultProps = {};

    render() {
        const { children, className } = this.props;

        const defaultSettings = {
            arrows: false,
            dots: true,
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1,
            adaptiveHeight: true,
            // autoplay: true,
            // autoplaySpeed: 2000,
            focusOnSelect: true,
            dotsClass: 'hero-dots',
            list
        };

        return (
            <Slider {...defaultSettings} className={`slider-hero ${className}`}>
                {children}
            </Slider>
        );
    }
}

export { HeroSlider };
