import classNames from 'classnames';
import React, {useState} from 'react';
import { Logo } from '../../../../../../components/Logo';
import logo from '../../../../../../assets/icons/Logo.svg';
import { Link } from 'react-scroll';


// Styles

import './styles.scss';

// ----------------

const AsideDrawer = ({ links, isOpen }) => {
  const modify = classNames({
    'aside--is-open': isOpen,
  });

  const [isAsideOpen, toggleAside] = useState(true);
  const closeAside = () => {
    toggleAside(!isAsideOpen);
  };


  return (
    <aside className={`aside ${modify}`}>
       <Logo maxWidth="254px" svg={logo}logoAside/>
      {links.map(({ label, to }, index) => {
        return (
          <div className="aside__item-wrapp" key={index}>
          <Link 
          to={to}
          activeClass="aside__item--active"
          className="aside__item"
          spy={true}
          smooth={true}
          offset={0}
          duration={500}
          isDynamic={true}>
            {label}
          </Link>
          </div>
        );
      })}
       {!"aside__item--active" && closeAside()}
    </aside>
  );
};

export default AsideDrawer;
